catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var system = g_form.getValue('systems_impacted');
    g_form.clearOptions('sub_system_impacted');
    g_form.hideFieldMsg('sub_system_impacted', true);


    var ga = new GlideAjax('getOutageSystems'); // Script Include Name
    ga.addParam('sysparm_name', 'getSubSystems'); // function in the script include 
    ga.addParam('sysparm_system', newValue);
    ga.getXML(informationParse); // The function to parse the return values

    function informationParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var subSystems = [];
        if (answer) {
            subSystems = answer.split(',');
            g_form.showFieldMsg('sub_system_impacted', 'Please select the related subsystem for ' + g_form.getValue('systems_impacted'));
        } else {
            //g_form.hideFieldMsg('sub_system_impacted', true);
            g_form.setDisplay('sub_system_impacted', false);
            //g_form.clearOptions('sub_system_impacted');
        }
        for (var i = 0; i < answer.length; i++) {
            g_form.setDisplay('sub_system_impacted', true);
            g_form.addOption('sub_system_impacted', subSystems[i], subSystems[i]);
        }
        // 		//answer = JSON.parse(answer);
        // 		for (var i = 0; i < answer.length; i++) { //Loop into the array
        // 			g_form.addOption('sub_system_impacted', answer[i], answer[i]);
        // 			alert(answer);
        // 		}
    }
}
