sys_script_include.script:
var getOutageSystems = Class.create();
getOutageSystems.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getSubSystems: function () {

		var system = this.getParameter('sysparm_system');
		//var subsystem = this.getParameter('sysparm_subsystem');

		var gr = new GlideRecord('x_mpii_bus_outage_critical_systems');
		gr.addQuery('system', system);
		gr.query();

		var subsystems = [];
		while (gr.next()) {
			subsystems.push(gr.subsystem.toString());
		}
		return subsystems.toString();
	},
	type: 'getOutageSystems'
});
